package com.goxod.freedom.utils

import java.util.*

object ApiUtil {

    fun ua(): String {
        return UA_POOL[randomIndex]
    }

    /**
     * 随机生成一个ip，防止被每日限制10个的机制拦截
     * */
    fun ip(): String {
        return mRandom.nextInt(255).toString() + "." + mRandom.nextInt(255)
            .toString() + "." + mRandom.nextInt(
            255
        ).toString() + "." + mRandom.nextInt(255).toString()
    }

    private val randomIndex: Int
        get() {
            val max = UA_POOL.size
            val min = 0
            val random = Random()
            return random.nextInt(max) % (max - min + 1) + min
        }

    private val mRandom = Random()

